import React, { useEffect } from 'react';

const Circles = ({ size, canvasRef }) => {
  const minRadius = 5;

  useEffect(() => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');

    canvas.width = size > 1440 ? size / 4 : size < 800 ? size / 2 : size < 600 && size - 20;
    canvas.height = size > 1440 ? size / 4 : size < 800 ? size / 2 : size < 600 && size - 20;

    const drawCircles = () => {
      const totalCircles = 2000;
      let createCircleAttempts = 500; // Changed to let

      const widthX = canvas.width;
      const heightY = canvas.width;
      const maxRadius = widthX / 4;

      const circles = [];

      const doesCircleHaveACollision = (circle) => {
        // Check for collision with other circles
        for (let i = 0; i < circles.length; i++) {
          const existingCircle = circles[i];
          const dx = circle.x - existingCircle.x;
          const dy = circle.y - existingCircle.y;
          const distance = Math.sqrt(dx * dx + dy * dy);
          if (distance <= circle.radius + existingCircle.radius) {
            return true;
          }
        }
        return false;
      };

      const createCircle = () => {
        const x = Math.random() * maxRadius;
        const y = Math.random() * maxRadius;
        const radius = minRadius + Math.random() * (maxRadius - minRadius);

        const newCircle = { x, y, radius };

        if (doesCircleHaveACollision(newCircle)) {
          // Retry if there's a collision
          if (createCircleAttempts > 0) {
            createCircleAttempts--;
            createCircle();
          }
          // Exit if retry limit is reached
          return;
        }

        circles.push(newCircle);
      };

      for (let i = 0; i < totalCircles; i++) {
        createCircle();
      }

      // Clear the canvas
      ctx.clearRect(0, 0, canvas.width, canvas.height);

      // Draw circles
      circles.forEach((circle) => {
        ctx.beginPath();

        ctx.arc(circle.x, circle.y, circle.radius, 0, 2 * Math.PI);
        ctx.strokeStyle = 'white';
        ctx.fillStyle = 'white';
        ctx.fill();
      });
    };

    // Set canvas size
    canvas.width = size;

    // Draw circles
    drawCircles();

    // Event listener for canvas click
    const handleCanvasClick = () => {
      drawCircles();
    };

    canvas.addEventListener('click', handleCanvasClick);

    // Clean up the event listener
    return () => {
      canvas.removeEventListener('click', handleCanvasClick);
    };
  }, [canvasRef, size]);

  return <canvas ref={canvasRef} />;
};

export default Circles;
