import React, { useEffect } from 'react';
import p5 from 'p5';

const Circles = ({ size, canvasRef }) => {
  let itter = 0;
  let circles = [];
  const minRadius = 5;
  const maxRadius = 300;
  const totalCircles = 2000;
  const createCircleAttempts = 500;
  const widthX = size / 4;
  const heightY = size / 4;
  const doesCircleHaveACollision = (circle) => {
    for (let i = 0; i < circles.length; i++) {
      let otherCircle = circles[i];
      let a = circle.radius + (otherCircle.radius / 4) * 1.976;
      let x = circle.x - otherCircle.x;
      let y = circle.y - otherCircle.y;

      if (a >= Math.sqrt(x * x + y * y)) {
        return true;
      }
    }

    if (circle.x + circle.radius >= widthX - 15 || circle.x - circle.radius <= 0) {
      return true;
    }

    if (circle.y + circle.radius >= heightY - 15 || circle.y - circle.radius <= 0) {
      return true;
    }

    return false;
  };

  const createAndDrawCircle = () => {
    let newCircle;
    let circleSafeToDraw = false;

    for (let tries = 0; tries < createCircleAttempts; tries++) {
      newCircle = {
        x: Math.random() * (widthX - 30) + 15,
        y: Math.random() * (heightY - 30) + 15,
        radius: minRadius,
      };

      if (doesCircleHaveACollision(newCircle)) {
        continue;
      } else {
        circleSafeToDraw = true;
        break;
      }
    }

    if (!circleSafeToDraw) {
      return;
    }

    for (let radiusSize = minRadius; radiusSize <= maxRadius; radiusSize++) {
      newCircle.radius = radiusSize;
      if (doesCircleHaveACollision(newCircle)) {
        newCircle.radius--;
        break;
      }
    }

    circles.push(newCircle);
    p5Instance.circle(newCircle.x, newCircle.y, newCircle.radius);
  };

  let p5Instance = null;

  const sketch = (p) => {
    p.setup = () => {
      p.createCanvas(widthX, heightY);
      p.background(0);
      p.translate(widthX / 2, heightY / 2);
      p.fill(255);
      p.circle(0, 0, widthX / 2);
      p.fill(0);
      p.textSize(45);
      p.textAlign(p.CENTER, p.CENTER);
      p.text('Click here', 0, 0);
    };

    p.draw = () => {
      if (p.mouseIsPressed) {
        circles = [];
        p.background(0);
        p.noFill();
        p.stroke(255);

        for (let i = 0; i < totalCircles; i++) {
          createAndDrawCircle();
        }

        itter += 1;
      }
    };
  };

  useEffect(() => {
    p5Instance = new p5(sketch);

    return () => {
      p5Instance.remove();
    };
  }, []);
  
};

export default Circles;
