import React, { useEffect, useRef } from 'react';

const Circles = ({ size }) => {
  const canvasRef = useRef(null);
  let itter = 0;
  let circles = [];
  const minRadius = 5;

  useEffect(() => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');
    const widthX = size / 3;
    const heightY = size / 3;
    const maxRadius = widthX / 4;

    const doesCircleHaveACollision = (circle) => {
      for (let i = 0; i < circles.length; i++) {
        let otherCircle = circles[i];
        let a = circle.radius + (otherCircle.radius / 4) * 1.976;
        let x = circle.x - otherCircle.x;
        let y = circle.y - otherCircle.y;
  
        if (a >= Math.sqrt(x * x + y * y)) {
          return true;
        }
      }
  
      if (circle.x + circle.radius >= widthX - 15 || circle.x - circle.radius <= 0) {
        return true;
      }
  
      if (circle.y + circle.radius >= heightY - 15 || circle.y - circle.radius <= 0) {
        return true;
      }
  
      return false;
    };

    const createAndDrawCircle = (x, y, radius) => {
      // Drawing logic
      // ...
    };

    const handleMouseClick = () => {
      // Clear canvas
      ctx.clearRect(0, 0, widthX, heightY);
      // Draw circles
      for (let i = 0; i < totalCircles; i++) {
        createAndDrawCircle();
      }
      itter += 1;
    };

    const totalCircles = 2000;

    canvas.width = widthX;
    canvas.height = heightY;

    ctx.fillStyle = 'black';
    ctx.fillRect(0, 0, widthX, heightY);
    ctx.fillStyle = 'white';
    ctx.strokeStyle = 'white';

    ctx.translate(widthX / 2, heightY / 2);
    ctx.arc(0, 0, widthX / 2, 0, 2 * Math.PI);
    ctx.fill();

    ctx.fillStyle = 'black';
    ctx.font = '45px Arial';
    ctx.textAlign = 'center';
    ctx.textBaseline = 'middle';
    ctx.fillText('Click here', 0, 0);

    canvas.addEventListener('click', handleMouseClick);

    return () => {
      canvas.removeEventListener('click', handleMouseClick);
    };
  }, [size]);

  return <canvas ref={canvasRef} />;
};

export default Circles;
