import { useState, useRef, useEffect } from 'react';
import { SketchPicker } from 'react-color';
import './BackgroundColorPickerComponent.css';

const BackgroundColorPickerComponent = ({ isVisible, colors, setColors }) => {
  const [isDragging, setIsDragging] = useState(false);
  const colorPickerRef = useRef(null);
  const isMounted = useRef(false);

  useEffect(() => {
    isMounted.current = true;
    return () => {
      isMounted.current = false;
    };
  }, []);

  const handleMouseDown = () => {
    setIsDragging(true);
  };

  const handleMouseUp = () => {
    setIsDragging(false);
  };

  const handleColorChange = (color) => {
    if (isMounted.current && !isDragging) {
      const newColor = color.hex;

      const colorExists = colors.includes(newColor);
      const isColorLimitReached = colors.length >= 4;

      if (!colorExists && !isColorLimitReached) {
        setColors([...colors, newColor]);
      }
    }
  };

  const handleColorPickerClick = () => {
    colorPickerRef.current.click();
  };

  return (
    isVisible && (
      <div className="sketchpicker background-sketchpicker" onClick={handleColorPickerClick}>
        <SketchPicker
          ref={colorPickerRef}
          color={colors[colors.length - 1]}
          onChange={handleColorChange}
          onMouseDown={handleMouseDown}
          onMouseUp={handleMouseUp}
          disableAlpha={true} // Optional: Disable the alpha slider if not needed
        />
      </div>
    )
  );
};

export default BackgroundColorPickerComponent;
