import React, { useEffect, useRef } from 'react';
import { useLocation } from 'react-router-dom';
import p5 from 'p5';

const TerrainAnimation = () => {
  const canvasRef = useRef(null);
  const location = useLocation();

  useEffect(() => {
    // p5.js sketch function
    const sketch = (p) => {
      let terrain = [];
      let flying = 0.0;
      let scl = 20; // Controls the scale of the terrain
      let cols, rows;

      p.setup = () => {
        p.createCanvas(p.windowWidth, p.windowHeight, p.WEBGL);

        // Calculate the number of columns and rows based on canvas size and scale
        cols = Math.floor(p.width / scl);
        rows = Math.floor(p.height / scl);

        // Initialize terrain array
        for (let x = 0; x < cols; x++) {
          terrain[x] = [];
        }
      };

      p.draw = () => {
        // Clear the canvas
        p.background(0);

        // Update the flying variable to control the terrain generation
        flying -= 0.1;

        let yoff = flying;
        for (let y = 0; y < rows; y++) {
          let xoff = 0;
          for (let x = 0; x < cols; x++) {
            // Use the noise function to generate the height of each vertex
            terrain[x][y] = p.map(p.noise(xoff, yoff), 0, 1, -50, 50);
            xoff += 0.5;
          }
          yoff += 0.5;
        }

        // Set up the camera and lighting for 3D rendering
        p.rotateX(p.PI / 3);
        p.translate(-p.width / 2, -p.height / 2);
        p.ambientLight(0);
        p.directionalLight(255, 255, 255, 0, 0, 1);

        // Render the terrain using triangles
        for (let y = 0; y < rows - 1; y++) {
          p.beginShape(p.TRIANGLE_STRIP);
          for (let x = 0; x < cols; x++) {
            // Calculate the vertices' positions
            p.vertex(x * scl, y * scl, terrain[x][y]);
            p.vertex(x * scl, (y + 1) * scl, terrain[x][y + 1]);
          }
          p.endShape();
        }
      };

      p.windowResized = () => {
        p.resizeCanvas(p.windowWidth, p.windowHeight);
      };
    };

    const terrainSketch = new p5(sketch, canvasRef.current);

    return () => {
      terrainSketch.remove();
    };
  }, [location]);

  return <div ref={canvasRef} className="terrain-animation" />;
};

export default TerrainAnimation;
