import React, { useEffect } from 'react';

const Circles = ({ size, canvasRef, isClicked, bgColor, strokeColor, fillColor }) => {
  useEffect(() => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');
    const minRadius = 2;
    canvas.width = size > 1440 ? 800 : size < 800 ? size / 2 : size < 600 && size - 20;
    canvas.height = size > 1440 ? 800 : size < 800 ? size / 2 : size < 600 && size - 20;

    const drawCircles = () => {
      const totalCircles = 2000;
      let createCircleAttempts = 500;

      const widthX = canvas.width;

      const circles = [];

      const doesCircleHaveACollision = (circle) => {
        for (let i = 0; i < circles.length; i++) {
          const existingCircle = circles[i];
          const dx = circle.x - existingCircle.x;
          const dy = circle.y - existingCircle.y;
          const distance = Math.sqrt(dx * dx + dy * dy);
          if (distance <= circle.radius + existingCircle.radius) {
            return true;
          }
        }
        return false;
      };

      const createCircle = () => {
        const x = Math.random() * widthX;
        const y = Math.random() * widthX;
        const radius = minRadius + Math.random() * (widthX - minRadius);

        const newCircle = { x, y, radius };

        if (doesCircleHaveACollision(newCircle)) {
          if (createCircleAttempts > 0) {
            createCircleAttempts--;
            createCircle();
          }

          return;
        }

        circles.push(newCircle);
      };

      ctx.fillStyle = bgColor;
      ctx.fillRect(0, 0, canvas.width, canvas.height);

      for (let i = 0; i < totalCircles; i++) {
        createCircle();
      }

      ctx.clearRect(0, 0, canvas.width, canvas.height);

      circles.forEach((circle) => {
        ctx.beginPath();
        ctx.arc(circle.x, circle.y, circle.radius, 0, 2 * Math.PI);
        ctx.strokeStyle = strokeColor;
        ctx.stroke();
      });
    };

    drawCircles();
  }, [isClicked]);

  return <canvas ref={canvasRef} />;
};

export default Circles;
