import React, { useEffect, useState } from 'react';

const Circles = ({ size, canvasRef }) => {
  const minRadius = 5;
  const [isClicked, setIsClicked] = useState(false);
  useEffect(() => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');

    canvas.width = size > 1440 ? size / 3 : size < 800 ? size / 2 : size < 600 && size - 20;
    canvas.height = size > 1440 ? size / 3 : size < 800 ? size / 2 : size < 600 && size - 20;

    const totalCircles = 2000;
    const createCircleAttempts = 500;
    const widthX = size;
    const heightY = size;
    const maxRadius = widthX / 4;

    const doesCircleHaveACollision = (circle) => {
      // Check for collision with other circles
      for (let i = 0; i < circles.length; i++) {
        let otherCircle = circles[i];
        let a = circle.radius + (otherCircle.radius / 4) * 1.976;
        let x = circle.x - otherCircle.x;
        let y = circle.y - otherCircle.y;

        if (a >= Math.sqrt(x * x + y * y)) {
          return true;
        }
      }

      // Check for collision with canvas boundaries
      if (circle.x + circle.radius >= widthX - 15 || circle.x - circle.radius <= 0) {
        return true;
      }

      if (circle.y + circle.radius >= heightY - 15 || circle.y - circle.radius <= 0) {
        return true;
      }

      return false;
    };

    const createAndDrawCircle = () => {
      let newCircle;
      let circleSafeToDraw = false;

      for (let tries = 0; tries < createCircleAttempts; tries++) {
        newCircle = {
          x: Math.random() * (widthX - 30) + 15,
          y: Math.random() * (heightY - 30) + 15,
          radius: minRadius,
        };

        if (doesCircleHaveACollision(newCircle)) {
          continue;
        } else {
          circleSafeToDraw = true;
          break;
        }
      }

      if (!circleSafeToDraw) {
        return;
      }

      for (let radiusSize = minRadius; radiusSize <= maxRadius; radiusSize++) {
        newCircle.radius = radiusSize;
        if (doesCircleHaveACollision(newCircle)) {
          newCircle.radius--;
          break;
        }
      }

      circles.push(newCircle);
      ctx.beginPath();
      ctx.arc(newCircle.x, newCircle.y, newCircle.radius, 0, 2 * Math.PI);
      ctx.closePath();
      ctx.fill();
    };

    let circles = [];

    const drawCircles = () => {
      ctx.clearRect(0, 0, canvas.width, canvas.height);

      if (isClicked) {
        circles = [];
        ctx.fillStyle = 'black';
        ctx.fillRect(0, 0, canvas.width, canvas.height);

        for (let i = 0; i < totalCircles; i++) {
          createAndDrawCircle();
        }
      }
    };

    drawCircles();
  }, [canvasRef, size]);

  return <canvas ref={canvasRef} />;
};

export default Circles;
