import { useState } from 'react';
import { SketchPicker } from 'react-color';
import './BackgroundColorPickerComponent.css';

const BackgroundColorPickerComponent = ({ isVisible, colors, setColors }) => {
  const [isDragging, setIsDragging] = useState(false);

  const handleMouseDown = () => {
    setIsDragging(true);
  };

  const handleMouseUp = () => {
    setIsDragging(false);
  };
  const handleColorChange = (color) => {
    // Check if the color picker is being dragged
    if (isDragging) {
      return; // Ignore color changes while dragging
    }

    const newColor = color.hex;

    // Check if the new color already exists in the colors array
    const colorExists = colors.includes(newColor);

    // Check if the maximum limit of four colors is reached
    const isColorLimitReached = colors.length >= 4;

    // Add the new color to the array only if it doesn't exist and the limit is not reached
    if (!colorExists && !isColorLimitReached) {
      setColors([...colors, newColor]);
    }
  };
  return (
    isVisible && (
      <div>
        <SketchPicker color={colors[colors.length - 1]} onChange={handleColorChange} />
      </div>
    )
  );
};

export default BackgroundColorPickerComponent;
