import React, { useState } from 'react';
import { ChromePicker } from 'react-color';
import './ColorPickerComponent.css';
const ColorPickerComponent = ({
  isBgVisable,
  isTriVisable,
  bgColor,
  setBgColor,
  triangleColor,
  setTriangleColor,
  triangleStrokeColor,
  setTriangleStrokeColor,
}) => {
  console.log('isBgVisable', isBgVisable);
  return (
    isBgVisable && (
      <div>
        <ChromePicker bgColor={bgColor} setBgColor={setBgColor} onChange={(updateColor) => setBgColor(updateColor.hex)} />
      </div>
    ),
    isTriVisable && (
      <div>
        <ChromePicker
          triangleColor={triangleColor}
          setTriangleColor={setTriangleColor}
          onChange={(updateColor) => setTriangleColor(updateColor.hex)}
        />
      </div>
    )
  );
};

export default ColorPickerComponent;
