import React, { useEffect, useRef } from 'react';

const Circles = () => {
  const sketchRef = useRef(null);

  useEffect(() => {
    const sketch = (p) => {
      let itter = 0;
      let circles = [];
      let minRadius = 5;
      let maxRadius = 300;
      let totalCircles = 2000;
      let createCircleAttempts = 500;
      let widthX = 500;
      let heightY = 500;

      p.setup = () => {
        p.createCanvas(widthX, heightY);
        p.background(0);
        p.translate(widthX / 2, heightY / 2);
        p.fill(255);
        p.circle(0, 0, widthX / 2);
        p.fill(0);
        p.textSize(45);
        p.textAlign(p.CENTER, p.CENTER);
        p.text('Click here', 0, 0);
      };

      p.draw = () => {
        if (p.mouseIsPressed) {
          circles = [];
          p.background(0);
          p.noFill();
          p.stroke(255);

          for (let i = 0; i < totalCircles; i++) {
            createAndDrawCircle();
          }

          itter += 1;
        }
      };

      function createAndDrawCircle() {
        let newCircle;
        let circleSafeToDraw = false;

        function doesCircleHaveACollision(circle) {
          for (let i = 0; i < circles.length; i++) {
            let otherCircle = circles[i];
            let a = circle.radius + (otherCircle.radius / 4) * 1.976;
            let x = circle.x - otherCircle.x;
            let y = circle.y - otherCircle.y;

            if (a >= Math.sqrt(x * x + y * y)) {
              return true;
            }
          }

          if (circle.x + circle.radius >= widthX - 15 || circle.x - circle.radius <= 0) {
            return true;
          }

          if (circle.y + circle.radius >= heightY - 15 || circle.y - circle.radius <= 0) {
            return true;
          }

          return false;
        }

        for (let tries = 0; tries < createCircleAttempts; tries++) {
          newCircle = {
            x: p.random(15, widthX - 15),
            y: p.random(15, heightY - 15),
            radius: minRadius,
          };

          if (doesCircleHaveACollision(newCircle)) {
            continue;
          } else {
            circleSafeToDraw = true;
            break;
          }
        }

        if (!circleSafeToDraw) {
          return;
        }

        for (let radiusSize = minRadius; radiusSize <= maxRadius; radiusSize++) {
          newCircle.radius = radiusSize;
          if (doesCircleHaveACollision(newCircle)) {
            newCircle.radius--;
            break;
          }
        }

        for (let radiusSize = minRadius; radiusSize < maxRadius; radiusSize++) {
          newCircle.radius = radiusSize;
          if (doesCircleHaveACollision(newCircle)) {
            newCircle.radius--;
            break;
          }
        }

        circles.push(newCircle);
        p.circle(newCircle.x, newCircle.y, newCircle.radius);
      }
    };

    return () => {
      sketchInstance.remove();
    };
  }, []);

  return <div ref={useRef}></div>;
};

export default Circles;
