        createAndDrawCircle();
      }
      itter += 1;
    };

    const totalCircles = 2000;

    canvas.width = widthX;
    canvas.height = heightY;

    ctx.fillStyle = 'black';
    ctx.tra
    ctx.fillRect(0, 0, widthX, heightY);
    ctx.fillStyle = 'white';
    ctx.strokeStyle = 'white';

    ctx.translate(widthX / 2, heightY / 2);
    ctx.arc(0, 0, widthX / 2, 0, 2 * Math.PI);
    ctx.fill();

    ctx.fillStyle = 'black';
    ctx.font = '45px Arial';
    ctx.textAlign = 'center';
    ctx.textBaseline = 'middle';
    ctx.fillText('Click here', 0, 0);

    canvas.addEventListener('click', handleMouseClick);

    return () => {
      canvas.removeEventListener('click', handleMouseClick);
    };
  }, [size]);

  return <canvas ref={canvasRef} />;
};

