import React, { useState } from 'react';
import { ChromePicker } from 'react-color';
import './ColorPickerComponent.css';
const BackgroundolorPickerComponent = ({
  isVisable,

  bgColor,
  setBgColor,
  triangleColor,
  setTriangleColor,
  triangleStrokeColor,
  setTriangleStrokeColor,
}) => {
  return (
    isVisable && (
      <div>
        <ChromePicker bgColor={bgColor} setBgColor={setBgColor} onChange={(updateColor) => setBgColor(updateColor.hex)} />
      </div>
    )
  );
