import React, { useState } from 'react';
import { Button, Typography, TextField, Popover } from '@mui/material';
import { createTheme  } from '@mui/material/styles';
import './RightsideMenu.css';
import ArrowBackIosRoundedIcon from '@mui/icons-material/ArrowBackIosRounded';
import ArrowForwardIosRoundedIcon from '@mui/icons-material/ArrowForwardIosRounded';

function RightsideMenu() {
  const [isRightMenuOpen, setIsRightMenuOpen] = useState(false);
  const [selectedColor, setSelectedColor] = useState('#ffffff');
  const [colorPickerAnchor, setColorPickerAnchor] = useState(null);

  const handleColorChange = (event) => {
    setSelectedColor(event.target.value);
  };

  const handleColorPickerOpen = (event) => {
    setColorPickerAnchor(event.currentTarget);
  };

  const handleColorPickerClose = () => {
    setColorPickerAnchor(null);
  };

  return (
    <>
      <div className={`right-menu__menu ${isRightMenuOpen ? 'open' : 'closed'}`}>
        <Button
          className={`right-menu__toggle ${isRightMenuOpen ? 'open' : ''}`}
          onClick={() => setIsRightMenuOpen(!isRightMenuOpen)}
        >
          {!isRightMenuOpen ? (
            <ArrowForwardIosRoundedIcon className="right-menu__icon" />
          ) : (
            <ArrowBackIosRoundedIcon className="right-menu__icon" />
          )}
        </Button>

        <div className="right-menu__elements">
          <Typography variant="h5" className="right-menu__colors-title">
            Colors
          </Typography>
          <Typography variant="subtitle1">Background Color</Typography>
          <div>
            <TextField value={selectedColor} onChange={handleColorChange} onClick={handleColorPickerOpen} />
            <Popover
              open={Boolean(colorPickerAnchor)}
              anchorEl={colorPickerAnchor}
              onClose={handleColorPickerClose}
              anchorOrigin={{
                vertical: 'bottom',
                horizontal: 'left',
              }}
              transformOrigin={{
                vertical: 'top',
                horizontal: 'left',
              }}
            >
              <createTheme color={selectedColor} onChange={(color) => setSelectedColor(color.hex)} />
              {/* You can use any color picker library or create your own */}
            </Popover>
          </div>

          <Typography variant="h5" className="right-menu__advanced-title">
            Advanced Triangles Settings
          </Typography>
          <ul>
            <li>Congestion</li>
            <li>Sequence</li>
            <li>Add Photo</li>
          </ul>
        </div>
      </div>
    </>
  );
}

export default RightsideMenu;
