// ...

const draw = () => {
  const imageData = ctx.getImageData(0, 0, canvas.width, canvas.height);
  const data = imageData.data;

  circles.forEach((circle) => {
    const x = Math.round(circle.x);
    const y = Math.round(circle.y);
    const radius = Math.round(circle.radius);

    // Check if the circle is large and overlaps more than one background color
    const hasLargeOverlap = radius > minRadius && checkOverlap(x, y, radius, imageData, bgColors);

    if (hasLargeOverlap) {
      // Flip colors where the background colors are split
      flipColors(x, y, radius, imageData, bgColors);
    }

    ctx.beginPath();
    ctx.arc(x, y, radius, 0, 2 * Math.PI);
    ctx.strokeStyle = strokeColor;
    ctx.fillStyle = fillColor;
    ctx.stroke();
    ctx.fill();
  });
};

const checkOverlap = (x, y, radius, imageData, bgColors) => {
  const data = imageData.data;

  let numBackgroundColors = 0;

  for (let dx = -radius; dx <= radius; dx++) {
    for (let dy = -radius; dy <= radius; dy++) {
      const pixelX = x + dx;
      const pixelY = y + dy;

      // Check if the pixel is within the canvas bounds
      if (pixelX >= 0 && pixelX < canvas.width && pixelY >= 0 && pixelY < canvas.height) {
        const pixelIndex = (pixelY * imageData.width + pixelX) * 4;
        const red = data[pixelIndex];
        const green = data[pixelIndex + 1];
        const blue = data[pixelIndex + 2];
        const pixelColor = `rgb(${red}, ${green}, ${blue})`;

        // Check if the pixel color matches any of the background colors
        if (bgColors.includes(pixelColor)) {
          numBackgroundColors++;

          // If there are more than one background colors, consider it as large overlap
          if (numBackgroundColors > 1) {
            return true;
          }
        }
      }
    }
  }

  return false;
};

const flipColors = (x, y, radius, imageData, bgColors) => {
  const data = imageData.data;

  for (let dx = -radius; dx <= radius; dx++) {
    for (let dy = -radius; dy <= radius; dy++) {
      const pixelX = x + dx;
      const pixelY = y + dy;

      // Check if the pixel is within the canvas bounds
      if (pixelX >= 0 && pixelX < canvas.width && pixelY >= 0 && pixelY < canvas.height) {
        const pixelIndex = (pixelY * imageData.width + pixelX) * 4;
        const red = data[pixelIndex];
        const green = data[pixelIndex + 1];
        const blue = data[pixelIndex + 2];
        const pixelColor = `rgb(${red}, ${green}, ${blue})`;

        // Check if the pixel color matches any of the background colors
        if (bgColors.includes(pixelColor)) {
          const flippedColor = getFlippedColor(pixelColor);
          const [flippedRed, flippedGreen, flippedBlue] = getColorComponents(flippedColor);

          // Flip the color of the pixel
          data[pixelIndex] = flippedRed;
          data[pixelIndex + 1] = flippedGreen;
          data[pixelIndex + 2] = flippedBlue;
        }
      }
    }
  }

  // Update the canvas with the modified pixel data
  ctx.putImageData(imageData, 0, 0);
};

const getFlippedColor = (color) => {
  // Implement your logic to flip the color
  // For example, if the color is white (#ffffff), flip it to black (#000000)
  // and vice versa
  if (color === '#ffffff') {
    return '#000000';
  } else if (color === '#000000') {
    return '#ffffff';
  } else {
    // Handle other colors if needed
    return color;
  }
};

const getColorComponents = (color) => {
  // Convert the color from hexadecimal to RGB components
  const red = parseInt(color.slice(1, 3), 16);
  const green = parseInt(color.slice(3, 5), 16);
  const blue = parseInt(color.slice(5, 7), 16);

  return [red, green, blue];
};

