import React, { useEffect, useState, useRef } from 'react';

const Sketch = () => {
  const [itter, setItter] = useState(0);
  const [circles, setCircles] = useState([]);
  const minRadius = 5;
  const maxRadius = 300;
  const totalCircles = 2000;
  const createCircleAttempts = 500;
  const widthX = 500;
  const heightY = 500;
  const canvasRef = useRef(null);

  useEffect(() => {
    const createAndDrawCircle = () => {
      let newCircle;
      let circleSafeToDraw = false;

      for (let tries = 0; tries < createCircleAttempts; tries++) {
        newCircle = {
          x: Math.random() * (widthX - 30) + 15,
          y: Math.random() * (heightY - 30) + 15,
          radius: minRadius,
        };

        const doesCircleHaveACollision = (circle) => {
          for (let i = 0; i < circles.length; i++) {
            let otherCircle = circles[i];
            let a = circle.radius + (otherCircle.radius / 4) * 1.976;
            let x = circle.x - otherCircle.x;
            let y = circle.y - otherCircle.y;

            if (a >= Math.sqrt(x * x + y * y)) {
              return true;
            }
          }

          if (circle.x + circle.radius >= widthX - 15 || circle.x - circle.radius <= 0) {
            return true;
          }

          if (circle.y + circle.radius >= heightY - 15 || circle.y - circle.radius <= 0) {
            return true;
          }

          return false;
        };

        if (doesCircleHaveACollision(newCircle)) {
          continue;
        } else {
          circleSafeToDraw = true;
          break;
        }
      }

      if (!circleSafeToDraw) {
        return;
      }

      for (let radiusSize = minRadius; radiusSize <= maxRadius; radiusSize++) {
        newCircle.radius = radiusSize;
        if (doesCircleHaveACollision(newCircle)) {
          newCircle.radius--;
          break;
        }
      }

      setCircles((prevCircles) => [...prevCircles, newCircle]);
    };

    const handleMousePress = () => {
      setCircles([]);
      setItter((prevItter) => prevItter + 1);
    };

    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');
    ctx.translate(widthX / 2, heightY / 2);

    const drawCircle = (circle) => {
      ctx.beginPath();
      ctx.arc(circle.x, circle.y, circle.radius, 0, 2 * Math.PI);
      ctx.fill();
      ctx.stroke();
      ctx.closePath();
    };

    const drawBackground = () => {
      ctx.fillStyle = '#000000';
      ctx.fillRect(-widthX / 2, -heightY / 2, widthX, heightY);
    };

    const drawText = () => {
      ctx.fillStyle = '#FFFFFF';
      ctx.font = '45px sans-serif';
      ctx.textAlign = 'center';
      ctx.fillText('Click here', 0, 0);
    };

    const draw = () => {
      drawBackground();
      drawText();

      for (let i = 0; i < totalCircles; i++) {
        drawCircle(circles[i]);
      }
    };

    canvas.addEventListener('mousedown', handleMousePress);

    const interval = setInterval(() => {
      drawBackground();
      drawText();

      for (let i = 0; i < totalCircles; i++) {
        drawCircle(circles[i]);
      }

      createAndDrawCircle();
    }, 100);

    return () => {
      clearInterval(interval);
      canvas.removeEventListener('mousedown', handleMousePress);
    };
  }, [itter, circles]);

  return <canvas ref={canvasRef} width={widthX} height={heightY} />;
};

export default Sketch;
