import { useState } from 'react';
import { SketchPicker } from 'react-color';
import './BackgroundColorPickerComponent.css';

const CustomSketchPickerWrapper = ({ children, onMouseDown }) => {
  const handleMouseDown = (e) => {
    e.stopPropagation(); // Prevent event bubbling
    onMouseDown();
  };

  return <div onMouseDown={handleMouseDown}>{children}</div>;
};

const BackgroundColorPickerComponent = ({ isVisible, colors, setColors }) => {
  const [isDragging, setIsDragging] = useState(false);

  const handleMouseDown = () => {
    setIsDragging(true);
    console.log('dragging');
  };

  const handleMouseUp = () => {
    setIsDragging(false);
  };

  const handleColorChange = (color) => {
    if (isDragging) {
      return;
    }

    const newColor = color.hex;

    const colorExists = colors.includes(newColor);
    const isColorLimitReached = colors.length >= 4;

    if (!colorExists && !isColorLimitReached) {
      setColors([...colors, newColor]);
    }
  };

  return (
    isVisible && (
      <div className="sketchpicker background-sketchpicker">
        <CustomSketchPickerWrapper onMouseDown={handleMouseDown}>
          <SketchPicker color={colors[colors.length - 1]} onChange={handleColorChange} onMouseUp={handleMouseUp} />
        </CustomSketchPickerWrapper>
      </div>
    )
  );
};

export default BackgroundColorPickerComponent;
