import React, { useState } from 'react';
import { ChromePicker } from 'react-color';
import './BackgroundColorPickerComponent.css';
const BackgroundColorPickerComponent = ({ isVisable, bgColor, setBgColor }) => {
  return (
    isVisable && (
      <div>
        <ChromePicker bgColor={bgColor} onChange={(newColor) => setBgColor(newColor.hex)} />
      </div>
    )
  );
};

export default BackgroundColorPickerComponent;
