import React, { useEffect } from 'react';
import p5 from 'p5';

const Circles = ({ size }) => {
  useEffect(() => {
    const sketch = (p) => {
      let canvas;
      let circles = [];

      p.setup = () => {
        canvas = p.createCanvas(size, size);
        p.noLoop();
        p.background(0); // Set initial background color
        drawCircles();
      };

      p.draw = () => {
        // Draw circles with sampled background colors
        circles.forEach((circle) => {
          const bg = p.get(circle.x, circle.y); // Sample background color at circle position
          const fillColor = p.color(bg[0], bg[1], bg[2]); // Create color object from sampled values
          p.fill(fillColor);
          p.stroke(255);
          p.ellipse(circle.x, circle.y, circle.radius * 2, circle.radius * 2);
        });
      };

      const drawCircles = () => {
        const totalCircles = 3000;

        for (let i = 0; i < totalCircles; i++) {
          const x = p.random(size);
          const y = p.random(size);
          const radius = p.random(10, 100); // Adjust the range of circle sizes as needed

          circles.push({ x, y, radius });
        }

        p.redraw(); // Trigger redraw to draw the circles
      };
    };

    const canvasParent = document.getElementById('canvas-parent');
    const p5Canvas = new p5(sketch, canvasParent);

    return () => {
      p5Canvas.remove(); // Clean up the p5.js canvas when the component unmounts
    };
  }, [size]);

  return <div id="canvas-parent"></div>;
};

export default Circles;
