import React, { useState } from 'react';
import { ChromePicker } from 'react-color';
import './ColorPickerComponent.css';
const ColorPickerComponent = ({
  isVisable,
  bgColor,
  setBgColor,
  triangleColor,
  setTriangleColor,
  triangleStrokeColor,
  setTriangleStrokeColor,
}) => {
  return (
    isVisable && (
      <div>
        <ChromePicker bgColor={bgColor} setBgColor={setBgColor} onChange={(updateColor) => setBgolor(updateColor.hex)} />
      </div>
    )
  );
};

export default ColorPickerComponent;
