import { useState, useRef, useEffect } from 'react';
import { SketchPicker } from 'react-color';
import './BackgroundColorPickerComponent.css';

const BackgroundColorPickerComponent = ({ isVisible, colors, setColors }) => {
  const [isDragging, setIsDragging] = useState(false);
  const isClickingRef = useRef(false);
  const colorPickerRef = useRef(null);

  const handleMouseDown = () => {
    setIsDragging(true);
    isClickingRef.current = true;
  };

  const handleMouseUp = () => {
    setIsDragging(false);
    if (isClickingRef.current) {
      isClickingRef.current = false;
    }
  };

  const handleColorChange = (color) => {
    if (isClickingRef.current) {
      const newColor = color.hex;

      const colorExists = colors.includes(newColor);
      const isColorLimitReached = colors.length >= 4;

      if (!colorExists && !isColorLimitReached) {
        setColors([...colors, newColor]);
      }
    }
  };

  useEffect(() => {
    const handleDocumentClick = (event) => {
      if (!colorPickerRef.current.contains(event.target)) {
        isClickingRef.current = false;
      }
    };
    document.addEventListener('click', handleDocumentClick);
    return () => {
      document.removeEventListener('click', handleDocumentClick);
    };
  }, []);

  const handleColorPickerClick = () => {
    isClickingRef.current = true;
    const popover = colorPickerRef.current.querySelector('.popover');
    const cover = colorPickerRef.current.querySelector('.cover');
    const clickEvent = new MouseEvent('mousedown', {
      view: window,
      bubbles: true,
      cancelable: true,
    });
    popover.dispatchEvent(clickEvent);
    cover.style.pointerEvents = 'none';
    setTimeout(() => {
      cover.style.pointerEvents = 'auto';
    }, 0);
  };

  return (
    isVisible && (
      <div className="sketchpicker background-sketchpicker" onClick={handleColorPickerClick}>
        <SketchPicker
          ref={colorPickerRef}
          color={colors[colors.length - 1]}
          onChange={handleColorChange}
          onMouseDown={handleMouseDown}
          onMouseUp={handleMouseUp}
        />
      </div>
    )
  );
};

export default BackgroundColorPickerComponent;
